    // const abfrageWetter = async () => {
    //     let success = false;
    //
    //     while (!success) {
    //         try {
    //             const response = await fetch('https://api.openweathermap.org/data/2.5/weather?q=l%C3%A4rz&units=metric&&appid=4d1adda53bf636a53408d0cd1c5ba7b4');
